import React, { useMemo, useState } from 'react';
import EndpointItem from './EndpointItem';
import userEndpoints from './userEndpoints'; // Tách endpoints ra file riêng

const User = ({ openEndpoint, setOpenEndpoint }) => {
  // Tối ưu hiệu suất với useMemo
  const endpoints = useMemo(() => userEndpoints, []);
  const [searchTerm, setSearchTerm] = useState('');
  
    const filteredEndpoints = endpoints.filter(
      (endpoint) =>
        endpoint.path.toLowerCase().includes(searchTerm.toLowerCase()) ||
        endpoint.method.toLowerCase().includes(searchTerm.toLowerCase())
    );

  return (
    
    <>
    <div className="search-container">
        <input
          type="text"
          className="search-input"
          placeholder="Tìm kiếm endpoint..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>
      {endpoints.map((endpoint, index) => (
        
        <EndpointItem
          key={`${endpoint.path}-${endpoint.method}-${index}`}
          endpoint={endpoint}
          index={index}
          openEndpoint={openEndpoint}
          setOpenEndpoint={setOpenEndpoint}
        />
      ))}
    </>
  );
};

export default User;
