import PSM from 'perfect-scrollbar';
import React, { useMemo, useState } from 'react';
import EndpointItem from './EndpointItem';
import commentEndpoints from './commentEndpoints';

const Comment = ({ openEndpoint, setOpenEndpoint }) => {
  // Tối ưu hiệu suất với useMemo
  const endpoints = useMemo(() => commentEndpoints, []);
  const [searchTerm, setSearchTerm] = useState('');

  // Lọc endpoint theo path hoặc method
  const filteredEndpoints = endpoints.filter(
    (endpoint) =>
      endpoint.path.toLowerCase().includes(searchTerm.toLowerCase()) ||
      endpoint.method.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="api-docs">
      <div className="search-container">
        <input
          type="text"
          className="search-input"
          placeholder="Tìm kiếm endpoint..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>
      {filteredEndpoints.length > 0 ? (
        filteredEndpoints.map((endpoint, index) => (
          <EndpointItem
            key={`${endpoint.path}-${endpoint.method}-${index}`}
            endpoint={endpoint}
            index={index}
            openEndpoint={openEndpoint}
            setOpenEndpoint={setOpenEndpoint}
          />
        ))
      ) : (
        <p>Không tìm thấy endpoint phù hợp.</p>
      )}
    </div>
  );
};

export default Comment;
